<script>
export default {
  methods:{
    goToShopper(){
      const nav = document.querySelector("#nav");
      nav.classList.remove('current-notes');
      nav.classList.remove('current-tasks');
      nav.classList.add('current-shopping');
      document.body.classList.add('view=Shopping');
      document.body.classList.remove('view=Notes');
      document.body.classList.remove('view=Tasks');
      document.body.classList.remove('addingSomething');
    },
    goToNotes(){
      const nav = document.querySelector("#nav");
      nav.classList.add('current-notes');
      nav.classList.remove('current-tasks');
      nav.classList.remove('current-shopping');
      document.body.classList.add('view=Notes');
      document.body.classList.remove('view=Shopping');
      document.body.classList.remove('view=Tasks');
      document.body.classList.remove('addingSomething');

    },
    goToTasks(){
      const nav = document.querySelector("#nav");
      nav.classList.remove('current-notes');
      nav.classList.add('current-tasks');
      nav.classList.remove('current-shopping');
      document.body.classList.add('view=Tasks');
      document.body.classList.remove('view=Notes');
      document.body.classList.remove('view=Shopping');
      document.body.classList.remove('addingSomething');
    },

  }
}
</script>

<style>
#nav{
  background: linear-gradient(60deg,rgb(0, 81, 255),rgb(0, 195, 255));
  display: flex;
  align-items: center;
  justify-content: center;
  position: fixed;
  bottom:20px ;
  height: 60px;
  width: 100%;
  left:50%;
  transform:translateX(-50%);
  bottom: 0px;
  z-index: 5;
  transition: 0.3s ease-out all;
  display:flex;
  box-shadow: 0px -2px 8px rgba(128, 128, 128, 0.623);
}
.note-expanded #nav{
  bottom:-20%;
}
#nav .icon{
  width:12%;
  fill:white;
  position:absolute;
}

.current-shopping #shoppingIcon{
  position:absolute;
  width:13%;
  animation: wiggle;
  animation-duration: 0.4s;
} 
.current-tasks #tasksIcon{
  position:absolute;
  width:13%;
  animation: wiggle;
  animation-duration: 0.4s;
} 
.current-notes #notesIcon{
  position:absolute;
  width:13%;
  animation-name: wiggle;
  animation-duration: 0.4s;
} 

@keyframes wiggle{
  0%{
    transform: rotate(0deg);
  }
  25%{
    transform: rotate(25deg);
  }
  50%{
    transform: rotate(-20deg);
  }
  75%{
    transform: rotate(10deg);

  }
  100%{
    transform: rotate(0deg);
  }
}

#shoppingIcon{
  left:10px;
}
#tasksIcon{
  right:10px;
}
.indicatorBar{
  position: absolute;
  width: 52px;
  height: 5px;
  bottom: 3px;
  background: rgb(255, 255, 255);
  border-radius: 5px;
  transition: 0.2s ease-in-out;
}
.current-shopping .indicatorBar{
  left:8px;
  right:inherit;
} 
.current-tasks .indicatorBar{
  left:auto;
  right:5px;
} 
.current-notes .indicatorBar{
  left:initial;
  right:inherit;
} 
</style>